import React, {useEffect, useState} from 'react';
import iconafrutta from "./images/iconafrutta.png";
import "./App.css";
const API_HOST = "http://localhost:3000";
const INVENTORY_API_URL = `${API_HOST}/frutti`;

function App() {
    const [data, setData] = useState([]);

    const [todos, setTodos] = React.useState([]);  
    const [isHidden,setHiddden] = React.useState(true);
    const [buttonAggiungiHidden,setButtonAggiungiHidden] = React.useState(false);
    const [buttonChiudiHidden,setButtonChiudiHidden] = React.useState(true);

    const [todo, setTodo] = React.useState("");  
    const [todo1, setTodo1] = React.useState("");
    const [todo2, setTodo2] = React.useState("");


    const fetchInventory = () => {
        fetch(`${INVENTORY_API_URL}`)
            .then(res => res.json())
            .then(json => setData(json));
    }

    useEffect(() => {
        fetchInventory();
    }, []);

    React.useEffect(() => {
      const data = localStorage.getItem("todos");
      const loadedTodos = JSON.parse(data);
      if (loadedTodos) {
        setTodos(loadedTodos);
      }
    }, []);

    React.useEffect(() => {
      const data = JSON.stringify(todos);
      localStorage.setItem("todos", data);
    }, [todos]);

    function handleSubmit(e) {
      e.preventDefault();

      const newTodo = {
        id: new Date().getTime(),
        imageSrc: iconafrutta,
        name:todo,
        qta:todo1,
        prezzo:todo2,

      };
      setData([...data].concat(newTodo));

      setTodo("");
      setTodo1("");
      setTodo2("");

    }

    function deleteTodo(id) {
      let updatedTodos = [...data].filter((todo) => todo.id !== id);
      setData(updatedTodos);
    }



    function isHiddenFunction(){
      setHiddden(false);
      setButtonAggiungiHidden(true);
      setButtonChiudiHidden(false);
    }

    function isViewableFunction(){
      setHiddden(true);
      setButtonAggiungiHidden(false);
      setButtonChiudiHidden(true);
    }

    function finishFunction(quantita){

      if (quantita>0){
        return "Disponibile"
      }
      else{

        return "Esaurito"
      }

    }
    function floatFunction(prezzo){

      if(prezzo==parseInt(prezzo)){
        return prezzo+",00"
      }
      else{
        return prezzo
      }

    }


    return (
      <div  id="image"> 

        <h1>My Fruit Shop</h1>    


        <div  id="todo-list">     

          <button hidden={buttonAggiungiHidden}  onClick={isHiddenFunction} > Aggiungi </button>
          <button hidden={buttonChiudiHidden} onClick={isViewableFunction}>Chiudi</button>

          <div hidden={isHidden}>                 

              <form   onSubmit={handleSubmit}>
                <img  src={iconafrutta} width="35" />

                <input
                  type="text"
                  placeholder="Descrizione prodotto"
                  onChange={(e) => setTodo(e.target.value)}
                  value={todo}
                  required
                />


                <input
                type="number"
                placeholder="Quantità"
                min="0"
                onChange={(e) => setTodo1(e.target.value)}
                value={todo1}
                required
                /> 

                <input
                  type="number"
                  placeholder="Prezzo"
                  min="0"
                  step="0.01"
                  onChange={(e) => setTodo2(e.target.value)}
                  value={todo2}
                  required
                /> 



              <button type="submit">Add Todo</button>


              </form>

           </div>       

          <div  id="container">

            <table width="1000px" >
                <thead>
                  <tr >
                      <th></th>
                      <th >Descrizione Prodotto</th>
                      <th>Qta</th>
                      <th>Prezzo</th>
                      <th>Stato</th>
                      <th>Azioni</th>
                  </tr>
                </thead>
                <tbody>


                      {
                              data.map((item) => (
                                  <tr key={item.id}>
                                      <td ><img width="60px" height="40px" src={item.imageSrc}/></td>
                                      <td>{item.name}</td>
                                      <td>{item.qta}</td>
                                      <td>{floatFunction(item.prezzo)}€</td>
                                      <td>{finishFunction(item.qta)}</td>
                                      <td><button id="buttonDelate"  onClick={() => deleteTodo(item.id)}></button></td>                                      
                                      <td/>
                                  </tr>
                              ))
                          }
                </tbody>
            </table>
          </div>

        </div>
    </div> 
    );
}

export default App;
