import React, {useEffect, useState} from 'react';

const API_HOST = "http://localhost:3000";
const INVENTORY_API_URL = `${API_HOST}/frutti`;

function App() {
    const [data, setData] = useState([]);

    const fetchInventory = () => {
        fetch(`${INVENTORY_API_URL}`)
            .then(res => res.json())
            .then(json => setData(json));
    }

    useEffect(() => {
        fetchInventory();
    }, []);




    /**
     *
     * @param id
     * @param newUnitPrice
     */
    const updateInventory = ({id, newUnitPrice}) => {
        fetch(`${INVENTORY_API_URL}/${id}`, {
            method: "PATCH",
            body: JSON.stringify({
                unit_price: newUnitPrice
            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
            .then(response => response.json())
            .then(json => {
                // reset inEditMode and unit price state values
                onCancel();

                // fetch the updated data
                fetchInventory();
            })
    }

    /**
     *
     * @param id -The id of the product
     * @param newUnitPrice - The new unit price of the product
     */
    const onSave = ({id, newUnitPrice}) => {
        updateInventory({id, newUnitPrice});
    }

    const onCancel = () => {
        // reset the inEditMode state value
        setInEditMode({
            status: false,
            rowKey: null
        })
        // reset the unit price state value
        setUnitPrice(null);
    }

    return (
      <div  className="container">

      <table width="800px" >
          <thead>
            <tr>
                <th></th>
                <th>Descrizione Prodotto</th>
                <th>Qta</th>
                <th>Prezzo</th>
                <th>Stato</th>
                <th>Azioni</th>
            </tr>
          </thead>
          <tbody>


                {
                        data.map((item) => (
                            <tr key={item.id}>
                                <td ><img width="60px" height="40px" src={item.imageSrc}/></td>
                                <td>{item.name}</td>
                                <td>{item.qta}</td>
                                <td>{item.prezzo}â‚¬</td>
                                <td>{item.qta}</td>
                                <td><button>cancella</button></td>                                      
                                <td/>
                            </tr>
                        ))
                    }
          </tbody>
      </table>
    </div>
    );
}

export default App;
