import React, {useEffect, useState} from 'react';

const API_HOST = "http://localhost:3000";
const INVENTORY_API_URL = `${API_HOST}/frutti`;

function App() {
    const [data, setData] = useState([]);

    const fetchInventory = () => {
        fetch(`${INVENTORY_API_URL}`)
            .then(res => res.json())
            .then(json => setData(json));
    }

    useEffect(() => {
        fetchInventory();
    }, []);




    return (
      <div  className="container">

      <table width="800px" >
          <thead>
            <tr>
                <th></th>
                <th>Descrizione Prodotto</th>
                <th>Qta</th>
                <th>Prezzo</th>
                <th>Stato</th>
                <th>Azioni</th>
            </tr>
          </thead>
          <tbody>


                {
                        data.map((item) => (
                            <tr key={item.id}>
                                <td ><img width="60px" height="40px" src={item.imageSrc}/></td>
                                <td>{item.name}</td>
                                <td>{item.qta}</td>
                                <td>{item.prezzo}â‚¬</td>
                                <td>{item.qta}</td>
                                <td><button>cancella</button></td>                                      
                                <td/>
                            </tr>
                        ))
                    }
          </tbody>
      </table>
    </div>
    );
}

export default App;
