import React, {useEffect, useState} from 'react';
import iconafrutta from "./images/iconafrutta.png"
const API_HOST = "http://localhost:3000";
const INVENTORY_API_URL = `${API_HOST}/frutti`;

function App() {
    const [data, setData] = useState([]);

    const [todos, setTodos] = React.useState([]);  
    const [isHidden,setHiddden] = React.useState(true);
    const [todo, setTodo] = React.useState("");  
    const [todo1, setTodo1] = React.useState("");
    const [todo2, setTodo2] = React.useState("");
    const [todoEditing, setTodoEditing] = React.useState(null);
    const [editingText, setEditingText] = React.useState("");

    const fetchInventory = () => {
        fetch(`${INVENTORY_API_URL}`)
            .then(res => res.json())
            .then(json => setData(json));
    }

    useEffect(() => {
        fetchInventory();
    }, []);

    React.useEffect(() => {
      const data = localStorage.getItem("todos");
      const loadedTodos = JSON.parse(data);
      if (loadedTodos) {
        setTodos(loadedTodos);
      }
    }, []);

    React.useEffect(() => {
      const data = JSON.stringify(todos);
      localStorage.setItem("todos", data);
    }, [todos]);

    function handleSubmit(e) {
      e.preventDefault();

      const newTodo = {
        id: new Date().getTime(),
        text: todo,
        text1: todo1,
        text2:todo1
      };
      setTodos([...todos].concat(newTodo));

      setTodo("");
      setTodo1("");
      setTodo2("");

    }

    function deleteTodo(id) {
      let updatedTodos = [...todos].filter((todo) => todo.id !== id);
      setTodos(updatedTodos);
    }





    function submitEdits(id) {
      const updatedTodos = [...todos].map((todo) => {
        if (todo.id === id) {
          todo.text = editingText;
        }
        return todo;
      });
      setTodos(updatedTodos);
      setTodoEditing(null);
    }


    function isHiddenFunction(){
      setHiddden(false)
    }

    function finishFunction(quantita){

      if (quantita>0){
        return "Disponibile"
      }
      else{

        return "Esaurito"
      }

    }


    return (
      <div  id="image"> 

        <h1>My Fruit Shop</h1>    


        <div  id="todo-list">     

          <button onClick={isHiddenFunction} > Aggiungi </button>

          <div hidden={isHidden}>                 

              <form   onSubmit={handleSubmit}>
                <img  src={iconafrutta} width="35" />

                <input
                  type="text"
                  placeholder="Descrizione prodotto"
                  onChange={(e) => setTodo(e.target.value)}
                  value={todo}
                />



                <input
                type="text"
                placeholder="Quantità"
                onChange={(e) => setTodo1(e.target.value)}
                value={todo1}
                /> 

                <input
                  type="text"
                  placeholder="prezzo"
                  onChange={(e) => setTodo2(e.target.value)}
                  value={todo2}
                /> 

              <button type="submit">Add Todo</button>

              </form>

           </div>       



        </div>
    </div> 
    );
}

export default App;
