import React, {useEffect, useState} from 'react';

const API_HOST = "http://localhost:3000";
const INVENTORY_API_URL = `${API_HOST}/frutti`;

function App() {
    const [data, setData] = useState([]);

    const fetchInventory = () => {
        fetch(`${INVENTORY_API_URL}`)
            .then(res => res.json())
            .then(json => setData(json));
    }

    useEffect(() => {
        fetchInventory();
    }, []);




    return (
      <div  id="image"> 

        <h1>My Fruit Shop</h1>    


        <div  id="todo-list">     

          <button onClick={aggiungiAAA} > Aggiungi </button>

          <div hidden={isHidden}>                 

              <form   onSubmit={handleSubmit}>
                <img  src={iconafrutta} width="35" />

                <input
                  type="text"
                  placeholder="Descrizione prodotto"
                  onChange={(e) => setTodo(e.target.value)}
                  value={todo}
                />



                <input
                type="text"
                placeholder="Quantità"
                onChange={(e) => setTodo1(e.target.value)}
                value={todo1}
                /> 

                <input
                  type="text"
                  placeholder="prezzo"
                  onChange={(e) => setTodo2(e.target.value)}
                  value={todo2}
                /> 

              <button type="submit">Add Todo</button>

              </form>

           </div>       

          <div  className="container">

            <table width="800px" >
                <thead>
                  <tr>
                      <th></th>
                      <th>Descrizione Prodotto</th>
                      <th>Qta</th>
                      <th>Prezzo</th>
                      <th>Stato</th>
                      <th>Azioni</th>
                  </tr>
                </thead>
                <tbody>


                      {
                              frutti.map((item) => (
                                  <tr key={item.id}>
                                      <td ><img width="60px" height="40px" src={item.imageSrc}/></td>
                                      <td>{item.name}</td>
                                      <td>{item.qta}</td>
                                      <td>{item.prezzo}€</td>
                                      <td>{finishFunction(item.qta)}</td>
                                      <td><button>cancella</button></td>                                      
                                      <td/>
                                  </tr>
                              ))
                          }
                </tbody>
            </table>
          </div>

        </div>
    </div> 
    );
}

export default App;
